        new() { Category = GameCategoryOptions.Classics, Name = "Contra", Likes = new Random().Next(50, 5000), Dislikes = new Random().Next(8, 500), Image = "https://i.blogs.es/d3fa4e/contra/450_1000.jpg", Description = "Contra es un videojuego de matamarcianos desarrollado y publicado por Konami, lanzado originalmente como un juego de arcade el 20 de febrero de 1987." },
        new() { Category = GameCategoryOptions.Classics, Name = "Super Mario", Likes = new Random().Next(50, 5000), Dislikes = new Random().Next(8, 500), Image = "https://pressover.news/wp-content/uploads/2021/09/71RuXB-P8lL._AC_SL1333_-1068x801.jpg", Description = "Super Mario es una serie de videojuegos de plataformas creados por la empresa desarrolladora Nintendo y protagonizados por su mascota, Mario." },
        new() { Category = GameCategoryOptions.Classics, Name = "Sonic The Hedgedog", Likes = new Random().Next(50, 5000), Dislikes = new Random().Next(8, 500), Image = "https://fs-prod-cdn.nintendo-europe.com/media/images/10_share_images/games_15/nintendo_3ds_download_software_7/SI_3DSDS_3DSonicTheHedgehog_image1600w.jpg", Description = "Sonic the Hedgehog es un videojuego desarrollado por Sonic Team y distribuido por Sega en 1991 para la videoconsola Sega Mega Drive protagonizado por Sonic y donde también hizo su debut. Este videojuego de plataformas fue, durante mucho tiempo, considerado el buque insignia de Sega, el ejemplo a seguir para sus futuros juegos. Incluso llegó a dar nombre a uno de sus equipos de desarrollo: Sonic Team.", IsFavourite = true },
        new() { Category = GameCategoryOptions.Classics, Name = "Aladdin", Likes = new Random().Next(50, 5000), Dislikes = new Random().Next(8, 500), Image = "https://m.media-amazon.com/images/I/81JplUUuSyL._AC_SL1500_.jpg", Description = "Disney's Aladdin es un videojuego de plataformas basado en la película de 1992 del mismo nombre desarrollada por Virgin Games USA. El juego fue lanzado por Sega para Sega Genesis el 11 de noviembre de 1993 como uno de varios juegos basados en la película, incluido otro juego que fue lanzado en el mismo mes por Capcom para Super NES." },
        new() { Category = GameCategoryOptions.Classics, Name = "Bomberman", Likes = new Random().Next(50, 5000), Dislikes = new Random().Next(8, 500), Image = "https://tn.com.ar/resizer/dxoMkqUJQ5O9UGgUHNyW051dJFE=/767x0/smart/filters:quality(60)/cloudfront-us-east-1.images.arcpublishing.com/artear/IGXEDYC6FTVTTCEVC7EIA4KO74.jpg", Description = "Bomberman es una franquicia de videojuegos estratégico-laberínticos originalmente desarrollada por Hudson Soft y actualmente por Konami. El juego original fue desarrollado por Shinichi Nakamoto y fue lanzado en 1983 para PC y posteriormente para Famicom.​ Desde entonces se han lanzado nuevos juegos." },
        new() { Category = GameCategoryOptions.Classics, Name = "Mahjong EZ", IsFavourite = true, Image = "https://gamesgamescdn.com/system/static/thumbs/spil_thumb_big/24353/webp_200X120_175581_1557746795.webp?1630789731", Description = "Esta es una versión del clásico juego de mesa que es ideal para jugadores principiantes y expertos por igual. ¿Qué tan rápido lograrás unir todas las fichas? Incluso puedes deshacer movimientos si piensas en una mejor jugada.", Likes = new Random().Next(50, 5000), Dislikes = new Random().Next(8, 500) },
        new() { Category = GameCategoryOptions.Classics, Name = "Ludo de 4 jugadores", Image = "https://gamesgamescdn.com/system/static/thumbs/spil_thumb_big/24550/webp_200X120_176291_1564410375.webp?1630789991", Description = "En esta versión del ludo, se puede jugar de hasta cuatro jugadores. Desafía a tus amigos y familiares en el clásico juego de mesa. ¿Lograrás llevar tus cuatro fichas hasta el triángulo de llegada antes que los demás?", Likes = new Random().Next(50, 5000), Dislikes = new Random().Next(8, 500) },
        new() { Category = GameCategoryOptions.Classics, Name = "Tic Tac Toe Paper", Image = "https://gamesgamescdn.com/system/static/thumbs/spil_thumb_big/24701/webp_200X120_176940_1570013609.webp?1630790187", Description = "En esta versión en línea del clásico juego puedes desafiar a la computadora o a un amigo. ¿Jugarás con las X o las O? ¡Intenta vencer a tu rival en un desafío al mejor de 3, 5 o 10!", Likes = new Random().Next(50, 5000), Dislikes = new Random().Next(8, 500) },
        new() { Category = GameCategoryOptions.Classics, Name = "Leyenda de las damas", Image = "https://gamesgamescdn.com/system/static/thumbs/spil_thumb_big/24338/webp_200X120_175511_1556787324.webp?1630789711", Description = "¿Lograrás convertirte en una leyenda en esta versión en línea del clásico juego de mesa? Enfréntate a diferentes oponentes virtuales mientras dominas los modos fácil, medio y difíciles.", Likes = new Random().Next(50, 5000), Dislikes = new Random().Next(8, 500) },
        new() { Category = GameCategoryOptions.Classics, Name = "Leyenda del solitario", IsFavourite = true, Image = "https://gamesgamescdn.com/system/static/thumbs/spil_thumb_big/24242/webp_200X120_175119_1553697527.webp?1630789585", Description = "¿Lograrás convertirte en una leyenda en esta versión en línea del clásico juego? No pierdas de vista el reloj mientras reúnen las cartas lo más rápido que puedas.", Likes = new Random().Next(50, 5000), Dislikes = new Random().Next(8, 500) },

        new() { Category = GameCategoryOptions.Click, Name = "Monkey Island 2", Likes = new Random().Next(50, 5000), Dislikes = new Random().Next(8, 500), Image = "https://cdn.cloudflare.steamstatic.com/steam/apps/32460/header.jpg?t=1571778532", Description = "Monkey Island 2: LeChuck's Revenge es una aventura gráfica realizada por LucasArts y publicada en diciembre de 1991,1 aunque la versión española lo hizo en abril de 1992,​ que fue realizada con el mismo motor que The Secret of Monkey Island, y fue distribuida originalmente en disquetes, con gráficos VGA para luego ser distribuida por separado y más tarde junto con la primera parte, en CD-ROM. El juego tiene un inesperado final, del que se ha dicho que es el más desconcertante de la historia de los videojuegos.", IsFavourite = true },
        new() { Category = GameCategoryOptions.Click, Name = "Broken Sword", Likes = new Random().Next(50, 5000), Dislikes = new Random().Next(8, 500), Image = "https://static0.gamerantimages.com/wordpress/wp-content/uploads/2020/10/pnc-broken-sword.jpg?q=50&fit=crop&w=740&dpr=1.5", Description = "Broken Sword es una serie de aventuras gráficas creada por el diseñador de videojuegos Charles Cecil de Revolution Software. Gira en torno a las aventuras de George Stobbart y Nicole Collard y sus búsquedas a lo largo de varios países partiendo de la historia y la mitología. Los dos primeros juegos se basaban en la clásica interfaz de point and click, que son gráficos realizados a mano, mientras que las dos últimas entregas fueron en formato 3D." },
        new() { Category = GameCategoryOptions.Click, Name = "Grim Fandango", Likes = new Random().Next(50, 5000), Dislikes = new Random().Next(8, 500), Image = "https://static0.gamerantimages.com/wordpress/wp-content/uploads/2020/10/pnc-grim-fandango.jpg?q=50&fit=crop&w=740&dpr=1.5", Description = "Grim Fandango es probablemente el clásico de apuntar y hacer clic más conocido de los años 90, y por una buena razón. Las aventuras de Manny a través de la tierra de los muertos están fuertemente inspiradas por una serie de influencias del mundo real, y son estas influencias las que hacen que la experiencia se sienta verdaderamente genuina. Bueno... tan genuina como puede ser una historia sobre personas esqueléticas, de todos modos." },
        new() { Category = GameCategoryOptions.Click, Name = "Beneath A Steel Sky", Likes = new Random().Next(50, 5000), Dislikes = new Random().Next(8, 500), Image = "https://static0.gamerantimages.com/wordpress/wp-content/uploads/2020/10/pnc-beneath-a-steel-sky.jpg?q=50&fit=crop&w=740&dpr=1.5", Description = "Como muchos de los títulos de esta era, Beneath a Steel Sky está comenzando a mostrar su edad en el departamento gráfico. Sin embargo, eso no quita nada a su fantástica historia y maravillosa actuación de voz, ni hace que los momentos de comedia bufonesca que se encuentran a lo largo del juego sean menos divertidos. En todo caso, solo sirve para enfatizar su brillantez." },
        new() { Category = GameCategoryOptions.Click, Name = "Myst", Likes = new Random().Next(50, 5000), Dislikes = new Random().Next(8, 500), Image = "https://static0.gamerantimages.com/wordpress/wp-content/uploads/2020/10/pnc-myst.jpg?q=50&fit=crop&w=740&dpr=1.5", Description = "A los hermanos Rand y Robyn Miller les tomó solo dos años convertir su idea de un juego de aventuras para adultos en una realidad, lo cual fue una hazaña impresionante considerando la escala y la ambición del proyecto. Tampoco se tomaron medidas a medias, y el resultado final resultó ser un éxito tanto para los jugadores como para los críticos.", IsFavourite = true },
        new() { Category = GameCategoryOptions.Click, Name = "Day of the Tentacle", Likes = new Random().Next(50, 5000), Dislikes = new Random().Next(8, 500), Image = "https://static0.gamerantimages.com/wordpress/wp-content/uploads/2020/10/pnc-dott.jpg?q=50&fit=crop&w=740&dpr=1.5", Description = "Tim Schafer a menudo es reconocido como el responsable de popularizar el crowdfunding de videojuegos , pero se podría argumentar que también es el responsable de la reciente reactivación de los juegos de aventura de apuntar y hacer clic. Ha trabajado en algunos de los mejores títulos que el género tiene para ofrecer, pero su primer papel protagónico llegó en Day of the Tentacle de 1993 ." },
        new() { Category = GameCategoryOptions.Click, Name = "Sam & Max Hit The Road", Likes = new Random().Next(50, 5000), Dislikes = new Random().Next(8, 500), Image = "https://static0.gamerantimages.com/wordpress/wp-content/uploads/2020/10/pnc-sam-and-max.jpg?q=50&fit=crop&w=740&dpr=1.5", Description = "Sam & Max Hit the Road fue uno de los primeros videojuegos en presentar protagonistas con voz completa, y el equipo de LucasArts no escatimó en gastos a la hora de contratar talentos. La decisión de apostar por actores de voz profesionales realmente valió la pena, ya que muchos críticos señalaron que la actuación de voz es una de las mayores fortalezas del juego." },
        new() { Category = GameCategoryOptions.Click, Name = "Full Throttle", Likes = new Random().Next(50, 5000), Dislikes = new Random().Next(8, 500), Image = "https://static0.gamerantimages.com/wordpress/wp-content/uploads/2020/10/pnc-full-throttle.jpg?q=50&fit=crop&w=740&dpr=1.5", Description = "La historia y la jugabilidad de Full Throttle son excelentes, pero es el protagonista principal del juego lo que realmente lo hace destacar. Ben Throttle es la definición misma de rudo, y el difunto gran Roy Conrad hizo un trabajo fantástico al darle vida al personaje. Está increíblemente bien escrito y sus técnicas creativas de resolución de problemas seguramente traerán una sonrisa a los rostros de los jugadores.", IsFavourite = true },
        new() { Category = GameCategoryOptions.Click, Name = "Loom", Likes = new Random().Next(50, 5000), Dislikes = new Random().Next(8, 500), Image = "https://static0.gamerantimages.com/wordpress/wp-content/uploads/2020/10/pnc-loom.jpg?q=50&fit=crop&w=740&dpr=1.5", Description = "A primera vista, puede que no sea obvio que Loom es un juego de LucasArts debido a las drásticas diferencias en el tono y la jugabilidad en comparación con sus otros títulos. En muchos sentidos, se siente más como un creador que experimenta con una variedad de ideas en conflicto que como un intento genuino de hacer un videojuego." },
        new() { Category = GameCategoryOptions.Click, Name = "Indiana Jones And The Fate Of Atlantis", Likes = new Random().Next(50, 5000), Dislikes = new Random().Next(8, 500), Image = "https://static0.gamerantimages.com/wordpress/wp-content/uploads/2020/10/pnc-ijatfoa.jpg?q=50&fit=crop&w=740&dpr=1.5", Description = "LucasArts, al ser una subsidiaria de LucasFilm, le dio al equipo acceso a algunas de las mejores licencias de películas de todos los tiempos. Además, a diferencia de muchos de los otros títulos con licencia que se hacían en esa época, los juegos de LucasArts basados ​​en películas eran realmente buenos. muy bueno Indiana Jones and the Fate of Atlantis es el ejemplo perfecto de la forma correcta de adaptar una franquicia cinematográfica a un videojuego." },

        new() { Category = GameCategoryOptions.Sports, Name = "Rocket League", Likes = new Random().Next(50, 5000), Dislikes = new Random().Next(8, 500), Image = "https://cdn.mos.cms.futurecdn.net/hnoJTWPk34kRZtcMCsrph5-970-80.jpg.webp", Description = "Algunas ideas son tan bellamente simples que es imposible imaginar un mundo sin ellas. Rocket League es uno de esos ejemplos. Irrumpiendo en la escena (y, memorablemente, gratis con PS Plus) en 2015, Rocket League ve equipos de hasta cuatro parachoques bloqueados en lo que es esencialmente 'fútbol con autos'.", IsFavourite = true },
        new() { Category = GameCategoryOptions.Sports, Name = "Lonely Mountains Downhill", Likes = new Random().Next(50, 5000), Dislikes = new Random().Next(8, 500), Image = "https://cdn.mos.cms.futurecdn.net/WsChgpnALbnvjnk4b5igEU-970-80.jpg.webp", Description = "El mejor juego de deportes al que nunca has jugado. Rectifica eso lo antes posible. Yuxtaponiendo muertes espantosas con un paisaje impresionante, corres a toda velocidad a un motorista cubo-hombre a través de cuatro montañas de recorridos desafiantes y duraderos. " },
        new() { Category = GameCategoryOptions.Sports, Name = "Tony Hawk’s Pro Skater 1", Likes = new Random().Next(50, 5000), Dislikes = new Random().Next(8, 500), Image = "https://cdn.mos.cms.futurecdn.net/bGRnemjgLoNtYGnAEg5jNc-970-80.jpg.webp", Description = "La nostalgia tiene la mala costumbre de jugarte malas pasadas. Enciende la serie original de Tony Hawk en PlayStation y es probable que tus recuerdos teñidos de rosa de beber Red Bull y escuchar Goldfinger en un bucle se desvanezcan. Es fangoso, complicado y carece de la fluidez de los esfuerzos futuros de THPS." },
        new() { Category = GameCategoryOptions.Sports, Name = "Art of Rally", Likes = new Random().Next(50, 5000), Dislikes = new Random().Next(8, 500), Image = "https://cdn.mos.cms.futurecdn.net/dTYf4hGB3vnDDYB49TGEEn-970-80.jpg.webp", Description = "Es una pena que Art of Rally no haya recibido la atención que merecía cuando se lanzó en septiembre de 2020, porque es uno de los juegos de carreras más estilísticos y hermosos hasta la fecha. Este es un juego independiente que se centra en la era dorada de los rallies, con muchos autos clásicos, incluido un número de la infame categoría del Grupo B." },
        new() { Category = GameCategoryOptions.Sports, Name = "Out Of The Park Baseball 22", Likes = new Random().Next(50, 5000), Dislikes = new Random().Next(8, 500), Image = "https://cdn.mos.cms.futurecdn.net/TBRFCa38kMLgGp3si6wvsB-970-80.jpg.webp", Description = "Fuera de los Estados Unidos, Football Manager ha sido considerado durante mucho tiempo el único juego en la ciudad en lo que respecta a los juegos de estrategia deportiva, pero hay un montón de simulador de banquillo acurrucado en ese punto ciego colectivo. OOTP es tan profundo como su contraparte de pelota de fútbol, ​​absorbiendo horas mientras negocia intercambios, promueve y degrada cargos entre clubes afiliados y se deleita en robar bases y aplastar jonrones.", IsFavourite = true },
        new() { Category = GameCategoryOptions.Sports, Name = "F1 2020", Likes = new Random().Next(50, 5000), Dislikes = new Random().Next(8, 500), Image = "https://cdn.mos.cms.futurecdn.net/T9RRdBBJERteoE3cu9hbrZ-970-80.png.webp", Description = "La franquicia oficial de Fórmula Uno ha pasado por un momento tumultuoso en la última década, y los fanáticos de la serie recordarán la decepción de F1 2014. Afortunadamente, ahora está de nuevo en plena forma y F1 2020 es uno de los mejores juegos de carreras que puedes comprar. Este Dia. Ofrece una experiencia de carreras muy diferente a un juego como Forza Horizon 4, pero si te gustan los autos de ultra alta velocidad con ese sonido de motor espeluznante que se precipita por los 22 circuitos únicos, la experiencia F1 2020 no tiene rival." },
        new() { Category = GameCategoryOptions.Sports, Name = "Fight Night Champion", Likes = new Random().Next(50, 5000), Dislikes = new Random().Next(8, 500), Image = "https://cdn.mos.cms.futurecdn.net/iECpY3LyjSX6fMHAzTo9wk-970-80.jpg.webp", Description = "Disponible en Xbox Series X gracias al programa de compatibilidad con versiones anteriores de Microsoft, Fight Night Champion es la recreación perfecta de la dulce ciencia. En un rincón, EA combina su excelente tratamiento habitual de los deportes alternativos con controles de precisión y gráficos que aún, hoy en día, pueden detenerlo en seco. En el otro, el Modo Campeón de Fight Night Champion. Anunciando una ola de narrativas completas que puntuaron los juegos deportivos durante la próxima década, la historia de Andre Bishop nunca ha sido mejor." },
        new() { Category = GameCategoryOptions.Sports, Name = "FIFA 22", Likes = new Random().Next(50, 5000), Dislikes = new Random().Next(8, 500), Image = "https://cdn.mos.cms.futurecdn.net/9ZRh2qX75kUwbucuun4GBg-970-80.jpg.webp", Description = "Pagar para ganar, guión, roto. FIFA no sería FIFA sin los jugadores haciendo cola para dispararle, y la mecánica de la caja de botín de apertura de paquetes en Ultimate Team sin duda necesita transparencia adicional. Sin embargo, considerado como un paquete, hay pocos juegos de deportes, o incluso juegos de cualquier género, que le den tanto a cambio de su dinero. ", IsFavourite = true },
        new() { Category = GameCategoryOptions.Sports, Name = "Super Mega Baseball 3", Likes = new Random().Next(50, 5000), Dislikes = new Random().Next(8, 500), Image = "https://cdn.mos.cms.futurecdn.net/TSGirX2CpZPAjW5VyLFatZ-970-80.jpg.webp", Description = "¿Béisbol? Sí. ¿Mega? Uh-uh. ¿Súper? Definitivamente. Cabezudos y jugadores ficticios hacen de este tercer esfuerzo de Metalhead Software una verdadera experiencia de regreso al futuro, que brinda mecánicas retro a un mundo HD. " },
        new() { Category = GameCategoryOptions.Sports, Name = "Football Manager 2022", Likes = new Random().Next(50, 5000), Dislikes = new Random().Next(8, 500), Image = "https://cdn.mos.cms.futurecdn.net/ZoJPWKEuLEpBrf42wbBkrd-970-80.jpg.webp", Description = "Tanto si creciste jugando Championship Manager como si acabas de adoptar la franquicia Football Manager, sabrás lo adictiva y precisa que es la simulación, que refleja el fútbol de la vida real. Todo, desde la negociación de transferencias y la oferta de nuevos contratos hasta la capacitación diaria y la interacción con la prensa, depende de usted, o no, si prefiere que el personal se encargue de todo. Si bien FM no te permite controlar directamente a los jugadores en el campo, es una simulación táctica mucho más completa que cualquier otra cosa en el mercado." },

        new() { Category = GameCategoryOptions.Puzzle, Name = "4 en linea", Likes = new Random().Next(50, 5000), Dislikes = new Random().Next(8, 500), Image = "https://my-live-01.slatic.net/p/5e1fa4498061348dfde1f5ccea5ad649.jpg", Description = "Conecta 4 (también conocido como 4 en Linea en algunas versiones) es un juego de mesa para dos jugadores distribuido por Hasbro, en el que se introducen fichas en un tablero vertical con el objetivo de alinear cuatro consecutivas de un mismo color.", IsFavourite = true, IsOutstanding = true, HowToPlay = "El objetivo del 4 en línea es alinear cuatro fichas sobre un tablero formado por seis filas y siete columnas. Cada jugador dispone de 21 fichas de un color (por lo general, rojas o amarillas). Por turnos, los jugadores deben introducir una ficha en la columna que prefieran (siempre que no esté completa) y ésta caerá a la posición más baja. Gana la partida el primero que consiga alinear cuatro fichas consecutivas de un mismo color en horizontal, vertical o diagonal.​ Si todas las columnas están llenas pero nadie ha hecho una fila válida, hay empate.", Controls = new() { "Click izquierdo - Seleccionar ficha", "Arrastrar mouse - Mover ficha", "Soltar click izquierdo - Colocar ficha" } },
        new() { Category = GameCategoryOptions.Puzzle, Name = "Professor Layton and the Miracle Mask", Likes = new Random().Next(50, 5000), Dislikes = new Random().Next(8, 500), Image = "https://cdn.mos.cms.futurecdn.net/adcb6ffe08a9a273b9f9d84dd95ae27b-970-80.jpg.webp", Description = "Esta serie de larga duración tiene muchos misterios, pero lo más destacado es esta historia del encantador profesor y su intento de comprender la verdad detrás del Caballero Enmascarado y los llamados milagros que está usando para dejar perplejos a los residentes de Monte d' O.", IsFavourite = true },
        new() { Category = GameCategoryOptions.Puzzle, Name = "Lara Croft Go", Likes = new Random().Next(50, 5000), Dislikes = new Random().Next(8, 500), Image = "https://cdn.mos.cms.futurecdn.net/iH5MAyCVQHPyQpADv2UA5A-970-80.png.webp", Description = "Estamos acostumbrados a ver a Lara en modo de acción, pero este encantador rompecabezas se centra en la investigación que también exige su trabajo de arqueología. El modo de juego sigue la fórmula establecida por los otros juegos de Go de Square Enix Montreal. Lara se enfrentará a todo, desde serpientes hasta arañas y púas en su camino para resolver el misterio de la Reina del Veneno. " },
        new() { Category = GameCategoryOptions.Puzzle, Name = "The Room", Likes = new Random().Next(50, 5000), Dislikes = new Random().Next(8, 500), Image = "https://cdn.mos.cms.futurecdn.net/nSNRYB2CXd4SDjRyQi6moE-970-80.jpg.webp", Description = "Este juego explora el significado de la vida y su relación con un quinto elemento extraño y espectral llamado Nulo. Como puede suponer, las cosas se ponen bastante raras bastante rápido. Pero también se vuelven muy divertidos. El estilo de arte gótico del juego crea inmediatamente el estado de ánimo para un descenso críptico a la locura, desbloqueando los secretos de muchos objetos intrincadamente detallados para descubrir lo que el antiguo habitante de la habitación sabía sobre Null." },
        new() { Category = GameCategoryOptions.Puzzle, Name = "The Swapper", Likes = new Random().Next(50, 5000), Dislikes = new Random().Next(8, 500), Image = "https://cdn.mos.cms.futurecdn.net/5KdmejKnsujwree5WWam8S-970-80.png.webp", Description = "No hay muchos juegos de rompecabezas que te hagan pensar seriamente en la vida, la conciencia y el significado del yo. Pero The Swapper despierta con éxito esas cavilaciones con sinceridad. Juegas como un astronauta que busca una gran estación de investigación abandonada, tratando de encontrar respuestas a dónde han ido los investigadores mientras encuentran un escape seguro. Recoges orbes para desbloquear nuevas áreas, lo que significa usar un dispositivo extraño para hacer copias de ti mismo para navegar por el diseño extraño de la estación.  " },
        new() { Category = GameCategoryOptions.Puzzle, Name = "The Talos Principle", Likes = new Random().Next(50, 5000), Dislikes = new Random().Next(8, 500), Image = "https://origin.mos.cms.futurecdn.net/zJjKUwAMYh82ANrqHj5nVG-970-80.jpg.webp", Description = "Otro excelente ejemplo de cómo combinar narrativa y rompecabezas, The Talos Principle es uno de los juegos más cerebrales que existen. Es el juego de una persona pensante, uno para los jugadores que quieren tener su juego con una gran cantidad de filosofía. ", IsFavourite = true },
