        new() { Category = GameCategoryOptions.Shots, Name = "Halo Infinite", Likes = new Random().Next(50, 5000), Dislikes = new Random().Next(8, 500), Image = "https://cdn.mos.cms.futurecdn.net/Ad8UeGJczS4MK6TVxjHbA9-970-80.jpg.webp", Description = "343 Industries tiene el peso del mundo sobre sus hombros con Halo Infinite. La serie ha tenido problemas para encontrar su lugar en la última década, y Halo Infinite tuvo que sentar bases sólidas para la futura expansión a medida que Xbox Game Studios busca una nueva generación. Y lo que recibimos fue bastante asombroso. La campaña lleva a Master Chief a una aventura de mundo abierto para conquistar y explorar una instalación de Halo, cumpliendo el sueño ofrecido por primera vez por Combat Evolved hace 20 años. El modo multijugador es igualmente emocionante, una oferta gratuita para PC, Xbox One y Xbox Series X que vuelve a las raíces de los juegos de arena de Halo 2 y Halo 3. Es un paquete fantástico." },
        new() { Category = GameCategoryOptions.Shots, Name = "Call of Duty Warzone", Likes = new Random().Next(50, 5000), Dislikes = new Random().Next(8, 500), Image = "https://elchapuzasinformatico.com/wp-content/uploads/2020/03/Call-of-Duty-Warzone.jpg", Description = "Call of Duty: Warzone transporta a los jugadores al corazón de la volátil batalla geopolítica de la Guerra Fría, a principios de la década de 1980." },
        new() { Category = GameCategoryOptions.Shots, Name = "Deathloop", Likes = new Random().Next(50, 5000), Dislikes = new Random().Next(8, 500), Image = "https://cdn.mos.cms.futurecdn.net/ALuGtnW5zFTU2udLMHJ4gB-970-80.jpg.webp", Description = "Deathloop es un muy buen momento. Es colorido y enérgico, elegante y cuidadosamente diseñado, lo que lo empuja a completar un ciclo perfecto de asesinatos en un solo día. Arkane tomó la experiencia que obtuvo al trabajar en Dishonored, Prey y Wolfenstein y la convirtió en un juego de disparos en primera persona verdaderamente dinámico, el tipo de experiencia que te mantendrá adivinando durante su larga duración. Los disparos se sienten fantásticos, es muy divertido luchar contra la IA y la variedad de poderes sobrenaturales que se ponen en tus manos solo ayudan a amplificar la carnicería. Deathloop es un juego obligatorio para todos los fanáticos de los juegos de disparos. " },
        new() { Category = GameCategoryOptions.Shots, Name = "Titanfall 2", Likes = new Random().Next(50, 5000), Dislikes = new Random().Next(8, 500), Image = "https://cdn.mos.cms.futurecdn.net/BtPSE5uKSwqcoLES6LHtAY-970-80.jpeg.webp", Description = "Titanfall 2 es un juego de disparos en primera persona subestimado de la última década. Es posible que Respawn Entertainment esté ocupado con Apex Legends ahora, pero si aún no lo ha jugado, se está perdiendo una de las mejores campañas en años. Es una historia sobre un piloto y su compañero robot mecánico, mientras exploran un mundo alienígena en constante disputa debido a las facciones enemigas. Corres por las paredes y te deslizas por el suelo, disparando a todo lo que ves con una serie de ingeniosas armas. Pero cada vez que te subes a tu robot, todo cambia, y ambas experiencias se vuelven una, ya que se entrelazan perfectamente entre sí. La historia ofrece niveles que siempre traen algo nuevo a la mesa, expandiendo las posibilidades de cada elemento del juego a niveles impensables." },
        new() { Category = GameCategoryOptions.Shots, Name = "Gears 5", Likes = new Random().Next(50, 5000), Dislikes = new Random().Next(8, 500), Image = "https://cdn.mos.cms.futurecdn.net/Pq2YaFwVNSjNTJNJYJXphF-970-80.jpg.webp", Description = "No sería una lista de los mejores tiradores sin al menos un juego de Gears. Gears 5, aunque tal vez no sea la primera opción para los veteranos de la serie, aún captura lo que hizo que la franquicia fuera tan interesante a lo largo de los años. Una versión aún más refinada de disparar a cubierto, combinada con nuevas armas y un grupo completamente nuevo de enemigos para luchar mientras la protagonista Kait Diaz descubre el Swarm en una nueva y emocionante historia. Pero puedes saltar directamente incluso si no has jugado ninguna de las entradas anteriores, especialmente si estás interesado en jugar en modo cooperativo con tus amigos. La adición de Escape, un modo para tres jugadores en el que el trabajo en equipo es obligatorio mientras te infiltras en una colmena enemiga, colocas explosivos y tienes que huir de la escena de inmediato, es una adición bienvenida a un panorama multijugador ya variado. Y en Gears 5, ciertamente hay algo para todos.", IsFavourite = true },
        new() { Category = GameCategoryOptions.Shots, Name = "PUBG: Battlegrounds", Likes = new Random().Next(50, 5000), Dislikes = new Random().Next(8, 500), Image = "https://cdn.mos.cms.futurecdn.net/XzFy5CXpR5edqJX3EDto4i-970-80.jpg.webp", Description = "El Battle Royale original es mejor que nunca. Si bien PlayerUnkown's Battlegrounds ha cedido algo de terreno a Apex Legends, Call of Duty: Warzones y Fortnites de este mundo, todavía tiene una ventaja táctica sobre su competencia más inmediata. Es más lento e implacable para los principiantes, con un mayor enfoque en la supervivencia, lo que solo ayuda a elevar los momentos de victoria incremental. Este juego de disparos en tercera persona recientemente se convirtió en un juego gratuito, imbuyendo a sus servidores con una nueva oportunidad de vida; hay una gran cantidad de contenido disponible en PUBG, desde una amplia gama de mapas hasta armas personalizables, y realmente deberías darle otra oportunidad si buscas un descanso de los otros Battle Royales principales. " },
        new() { Category = GameCategoryOptions.Shots, Name = "Half-Life Alyx", Likes = new Random().Next(50, 5000), Dislikes = new Random().Next(8, 500), Image = "https://cdn.mos.cms.futurecdn.net/sxSWinExz2y6HB76Rgijqh-970-80.jpg.webp", Description = "Muchos señalarán a Half-Life 2 como uno de los mejores juegos de disparos de todos los tiempos y, si bien sigue siendo una experiencia fantástica y formativa, hay verdadera magia en Half-Life Alyx. Esta exclusiva de realidad virtual muestra el poder y el potencial de la realidad virtual para los tiradores, brindándole un nivel de control y libertad sin precedentes para experimentar en un mundo al que estábamos tan desesperados por regresar. Al darnos un control complejo sobre las armas en nuestras manos y al diseñar escenarios verdaderamente reactivos, Valve pudo ofrecer uno de los mejores juegos de disparos de todos los tiempos. Confía en nosotros cuando te decimos que nunca has jugado nada tan inmersivo como Half-Life Alyx , un juego imprescindible si puedes conseguir la tecnología necesaria para ejecutarlo. " },
    };

    public async Task<List<GameCategory>> GetCategoriesWithGames()
    {
        await Task.Delay(1000);
        var gameCategories = new List<GameCategory>();
        gameCategories.Add(new() { Name = "Acción", Games = _games.Where(x => x.Category == GameCategoryOptions.Action).ToList() });
        gameCategories.Add(new() { Name = "Aventura", Games = _games.Where(x => x.Category == GameCategoryOptions.Adventure).ToList() });
        gameCategories.Add(new() { Name = "Belleza", Games = _games.Where(x => x.Category == GameCategoryOptions.Beauty).ToList() });
        gameCategories.Add(new() { Name = "Carrera", Games = _games.Where(x => x.Category == GameCategoryOptions.Races).ToList() });
        gameCategories.Add(new() { Name = "Clásicos", Games = _games.Where(x => x.Category == GameCategoryOptions.Classics).ToList() });
        gameCategories.Add(new() { Name = "Click", Games = _games.Where(x => x.Category == GameCategoryOptions.Click).ToList() });
        gameCategories.Add(new() { Name = "Deportes", Games = _games.Where(x => x.Category == GameCategoryOptions.Sports).ToList() });
        gameCategories.Add(new() { Name = "Puzzle", Games = _games.Where(x => x.Category == GameCategoryOptions.Puzzle).ToList() });
        gameCategories.Add(new() { Name = "Tiros", Games = _games.Where(x => x.Category == GameCategoryOptions.Shots).ToList() });
        return gameCategories;
    }

    public async Task<List<Game>> GetFavouriteGamesFromUser()
    {
        await Task.Delay(1000);
        return _games.Where(x => x.IsFavourite).ToList();
    }

    public async Task<Game?> GetGame(string name)
    {
        await Task.Delay(1000);
        foreach (var game in _games)
            if (game.Name.ToLower().Contains(name.ToLower()))
                return game;
        return null;
    }

    public async Task<Game?> GetOutstandingGame()
    {
        await Task.Delay(1000);
        foreach (var game in _games)
            if (game.IsOutstanding)
                return game;
        return null;
    }

    public async Task<List<Game>> SearchGames(string name, GameFilters? filters = null)
    {
        await Task.Delay(1000);
        var games = new List<Game>();
        foreach (var game in _games)
            if (game.Name.ToLower().Contains(name.ToLower()))
                games.Add(game);

        if (filters is null)
            return games;

        if (!string.IsNullOrWhiteSpace(filters.Category))
            games = games.Where(x => string.Compare(x.Category, filters.Category, CultureInfo.CurrentCulture, CompareOptions.IgnoreNonSpace | CompareOptions.IgnoreCase) == 0).ToList();

        if (!string.IsNullOrWhiteSpace(filters.Popularity))
        {
            if (filters.Popularity == GamePopularityOptions.MorePopulars)
                games = games.OrderByDescending(x => x.Likes).ToList();
            else if (filters.Popularity == GamePopularityOptions.LessPopulars)
                games = games.OrderByDescending(x => x.Dislikes).ToList();
        }

        if (!string.IsNullOrWhiteSpace(filters.Order))
        {
            if (filters.Order == GameOrderOptions.Ascendant)
                games = games.OrderBy(x => x.Name).ToList();
            else if (filters.Order == GameOrderOptions.Descendant)
                games = games.OrderByDescending(x => x.Name).ToList();
        }
            
        return games;
    }
}
